mapper.update:
update shopping_cart

















		, number = #{number}






	user_id = #{userId}

		and dish_id = #{dishId}


		and setmeal_id = #{setmealId}

mapper.delete:
delete from shopping_cart

	user_id = #{userId}

		and dish_flavor = #{dishFlavor}


		and dish_id = #{dishId}


		and setmeal_id = #{setmealId}

mapper.select:
select id, name, image, user_id, dish_id, setmeal_id, dish_flavor, number, amount, create_time
from shopping_cart

	user_id = #{userId}

		and dish_id = #{dishId}


		and setmeal_id = #{setmealId}
